import React, { useState } from 'react';
import './App.css';

function App() {
  const [foods, setFoods] = useState([]);
  const [newFood, setNewFood] = useState('');
  const [orders, setOrders] = useState([]);

  const handleAddFood = () => {
    if (newFood.trim() !== '') {
      setFoods([...foods, { name: newFood, price: getRandomPrice() }]);
      setNewFood('');
    }
  };

  const handleRemoveFood = (index) => {
    const updatedFoods = [...foods];
    updatedFoods.splice(index, 1);
    setFoods(updatedFoods);
  };

  const handleOrderFood = (food) => {
    setOrders([...orders, food]);
  };

  const getRandomPrice = () => {
    return Math.floor(Math.random() * 1000) + 100;
  };

  return (
    <div className="app">
      <h1>Food Management System</h1>
      <div className="food-list">
        <h2>Food List</h2>
        <div className="food-cards">
          {foods.map((food, index) => (
            <div key={index} className="food-card">
              <div className="food-details">
                <h3>{food.name}</h3>
                <p>Price: ₹{food.price}</p>
              </div>
              <div className="food-actions">
                <button onClick={() => handleRemoveFood(index)}>Remove</button>
                <button onClick={() => handleOrderFood(food)}>Order</button>
              </div>
            </div>
          ))}
        </div>
      </div>
      <div className="add-food">
        <h2>Add Food</h2>
        <input
          type="text"
          value={newFood}
          onChange={(e) => setNewFood(e.target.value)}
          placeholder="Enter food name"
        />
        <button onClick={handleAddFood}>Add Food</button>
      </div>
      <div className="order-page">
        <h2>Order Page</h2>
        <ul>
          {orders.map((order, index) => (
            <li key={index}>{order.name} - ₹{order.price}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
